const navToggleIcon=document.querySelector(".btn-menu"),menu=document.querySelector(".menu"),cover=document.querySelector(".cover"),panelsItems=document.querySelectorAll(".panels-item");function navigationTabsInit(e,t,s){e.forEach(e=>{e.addEventListener("click",function(){removeActiveClass(t),removeActiveClass(s),this.classList.add(t);let e=this.getAttribute("data-content-id");document.querySelector(e).classList.add(s)})})}function removeActiveClass(e){document.querySelector(`.${e}`).classList.remove(e)}navToggleIcon.addEventListener("click",function(){this.classList.toggle("btn-menu--active"),menu.classList.toggle("menu--show"),cover.classList.toggle("cover--show")}),navigationTabsInit(panelsItems,"panels-item--active","package--show");